        { id: 'hcp2', ten_gop_hco: 'Tr·∫ßn Th·ªã B - B·ªánh vi·ªán 115' },
        { id: 'hcp3', ten_gop_hco: 'L√™ VƒÉn C - B·ªánh vi·ªán ƒê·∫°i h·ªçc Y D∆∞·ª£c' },
