            <p className="fw-bold mt-3">Chi ph√≠ d·ª± ki·∫øn</p>
            <FloatingLabel label="Chi ph√≠ thu√™ h·ªôi tr∆∞·ªùng" className="border rounded mt-2">
                <Form.Control type="number" value={chiPhiHoiTruong} onChange={e => setChiPhiHoiTruong(e.target.value)} />
            </FloatingLabel>
            <FloatingLabel label="Chi ph√≠ thu√™ m√°y chi·∫øu" className="border rounded mt-2">
                <Form.Control type="number" value={chiPhiMayChieu} onChange={e => setChiPhiMayChieu(e.target.value)} />
            </FloatingLabel>
            <FloatingLabel label="Chi ph√≠ ƒÉn u·ªëng" className="border rounded mt-2">
