            </FloatingLabel>
             <FloatingLabel label="Chi ph√≠ m·ªùi b√°o c√°o vi√™n" className="border rounded mt-2">
                <Form.Control type="text" value={chiPhiBaoCaoVien} onChange={e => setChiPhiBaoCaoVien(e.target.value)} />
            </FloatingLabel>
             <FloatingLabel label="T·∫∑ng ph·∫©m" className="border rounded mt-2">
