                frameBorder="0"
                style={{ border: 0 }}
                allowFullScreen
            ></iframe>
        </div>
    );

    return (
        <Container fluid className="bg-teal-100 h-100 p-2">
            <Row className="justify-content-center">
                <Col xs={12}>
                    <Nav variant="pills" fill className="bg-light p-2 rounded gap-2 fw-bold">
                        <Nav.Item>
                            <Nav.Link 
                                onClick={() => setActiveTab('deXuat')} 
                                className={activeTab === 'deXuat' ? 'bg-merap-active text-white' : 'bg-white shadow-sm border text-dark'}
                            >
                                ƒê·ªÅ xu·∫•t
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link 
                                onClick={() => setActiveTab('cxmDuyet')}
                                className={activeTab === 'cxmDuyet' ? 'bg-merap-active text-white' : 'bg-white shadow-sm border text-dark'}
                            >
                                CXM Duy·ªát
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link 
                                onClick={() => setActiveTab('baoCao')}
                                className={activeTab === 'baoCao' ? 'bg-merap-active text-white' : 'bg-white shadow-sm border text-dark'}
                            >
                                B√°o c√°o
                            </Nav.Link>
                        </Nav.Item>
                    </Nav>

                    {activeTab === 'deXuat' && renderDeXuatTab()}
                    {activeTab === 'cxmDuyet' && renderCxmDuyetTab()}
                    {activeTab === 'baoCao' && renderBaoCaoTab()}
                </Col>
            </Row>
        </Container>
    );
};

export default FormSeminarHcp;

